import React,{Component} from 'react';
import logo from './logo.svg';
import './App.css';
import { render } from 'react-dom';
import Person from './Person/person';


class App extends Component {

  state = {
    persons:[
      {name:'Max',age:28},
      {name:'Nanu',age:25},
      {name:'Ok',age:24},
    ],
    otherState:'some value',
    showPerson:false
  }

  switchNameHandler = (event) =>{
    // console.log(this.state);
    this.setState({
      persons:[
        {name:'Maximilian',age:30},
        {name:'Nanu',age:28},
        {name: event.targe.value,age:28},
      ]
    })
  }

  clickHandler = () =>{
    this.setState({
      persons:[
        {name:'Maximilian',age:30},
        {name:'Nanu',age:28},
        {name:"daniel zeng",age:28},
      ]
    })
  }

  togglePersonsHandler = () => {

  }

  render(){
    return (
          <div className="App">
            <div>
              <button onClick={this.clickHandler}>swith name</button>
            <Person click={this.clickHandler}
            name={this.state.persons[0].name}
            age={this.state.persons[0].age}
            changed={this.switchNameHandler.bind(this,"daniel")}
            />
          <Person click={this.clickHandler}
            name={this.state.persons[1].name}
            age={this.state.persons[1].age}
            // changed={this.switchNameHandler}
            >Hello!!</Person>
          <Person click={this.clickHandler}
            name={this.state.persons[2].name}
            age={this.state.persons[2].age} 
            // changed={(evnent)=>{this.switchNameHandler(evnent)}}
            >Hello!!</Person>
            </div>

          </div>
    );
  }
}

export default App;
