export const simpleAction = () => dispatch => {
  fetch("https://dog.ceo/api/breeds/list/all").then(response => {
    console.log(JSON.stringify(response));
    //   return response.json();
    dispatch({
      type: "SIMPLE_ACTION",
      payload: response
    });
  });
};
