export const fetchDogsAction = () => dispatch => {
  fetch("https://dog.ceo/api/breeds/list/all")
    .then(response => {
      //   console.log(JSON.stringify(response));
      return response.json();
    })
    .then(data => {
      console.log(data);

      dispatch({
        type: "FETCH_DOGS_ACTION",
        payload: data
      });
    });
};

export const imageAction = name => dispatch => {
  fetch(`https://dog.ceo/api/breed/${name}/images`)
    .then(response => {
      //   console.log(JSON.stringify(response));
      return response.json();
    })
    .then(data => {
      console.log(data);

      dispatch({
        type: "IMAGE_ACTION",
        payload: data
      });
    });
};

export const imagesAction = names => {
  return Promise.all(
    names.map(name => {
      imageAction(name);
    })
  );
};
